(6, '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n  <title>Nhận thẻ cào miễn phí</title>\r\n  <style>\r\n    body {\r\n      font-family: Arial, sans-serif;\r\n      background-color: #f2f2f2;\r\n      margin: 0;\r\n      padding: 0;\r\n    }\r\n\r\n    .container {\r\n      max-width: 400px;\r\n      margin: 50px auto;\r\n      background-color: #fff;\r\n      padding: 20px;\r\n      border-radius: 5px;\r\n      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);\r\n    }\r\n\r\n    h1 {\r\n      text-align: center;\r\n      color: #333;\r\n    }\r\n\r\n    form {\r\n      margin-top: 20px;\r\n    }\r\n\r\n    label {\r\n      display: block;\r\n      margin-bottom: 8px;\r\n      color: #666;\r\n    }\r\n\r\n    input[type=\"text\"],\r\n    input[type=\"email\"] {\r\n      width: 100%;\r\n      padding: 10px;\r\n      border: 1px solid #ccc;\r\n      border-radius: 4px;\r\n      box-sizing: border-box;\r\n    }\r\n\r\n    input[type=\"submit\"] {\r\n      width: 100%;\r\n      background-color: #4CAF50;\r\n      color: #fff;\r\n      border: none;\r\n      padding: 12px;\r\n      margin-top: 20px;\r\n      border-radius: 4px;\r\n      cursor: pointer;\r\n    }\r\n\r\n    input[type=\"submit\"]:hover {\r\n      background-color: #45a049;\r\n    }\r\n\r\n    .networks {\r\n      display: flex;\r\n      justify-content: space-between;\r\n      margin-top: 20px;\r\n    }\r\n\r\n    .networks img {\r\n      max-height: 50px;\r\n      max-width: 100px;\r\n      cursor: pointer;\r\n    }\r\n\r\n    .denominations {\r\n      margin-top: 20px;\r\n    }\r\n\r\n    .denominations label {\r\n      display: inline-block;\r\n      margin-right: 10px;\r\n    }\r\n\r\n    /* Modal styles */\r\n    .modal {\r\n      display: none;\r\n      position: fixed;\r\n      z-index: 1;\r\n      left: 0;\r\n      top: 0;\r\n      width: 100%;\r\n      height: 100%;\r\n      overflow: auto;\r\n      background-color: rgba(0, 0, 0, 0.5);\r\n    }\r\n\r\n    .modal-content {\r\n      background-color: #fefefe;\r\n      margin: 20% auto;\r\n      padding: 20px;\r\n      border: 1px solid #888;\r\n      width: 80%;\r\n      border-radius: 5px;\r\n      text-align: center;\r\n    }\r\n\r\n    .close {\r\n      color: #aaa;\r\n      float: right;\r\n      font-size: 28px;\r\n      font-weight: bold;\r\n      cursor: pointer;\r\n    }\r\n\r\n    .close:hover,\r\n    .close:focus {\r\n      color: #000;\r\n      text-decoration: none;\r\n      cursor: pointer;\r\n    }\r\n  </style>\r\n</head>\r\n<body>\r\n  <div class=\"container\">\r\n    <h1>Nhận thẻ cào miễn phí</h1>\r\n    <form>\r\n      <label for=\"name\">Số điện thoại:</label>\r\n      <input type=\"text\" id=\"name\" name=\"name\" required>\r\n\r\n      <div class=\"networks\">\r\n        <label for=\"viettel\"><img src=\"https://upload.wikimedia.org/wikipedia/commons/thumb/f/fe/Viettel_logo_2021.svg/2560px-Viettel_logo_2021.svg.png\" alt=\"Viettel\"></label>\r\n        <label for=\"mobifone\"><img src=\"https://cdn.haitrieu.com/wp-content/uploads/2022/01/Logo-Mobifone.png\" alt=\"Mobifone\"></label>\r\n        <label for=\"vinaphone\"><img src=\"https://upload.wikimedia.org/wikipedia/commons/e/ed/Logo_Vinaphone.svg\" alt=\"Vinaphone\"></label>\r\n      </div>\r\n\r\n      <div class=\"denominations\">\r\n        <label for=\"denomination1\"><input type=\"radio\" id=\"denomination1\" name=\"denomination\" value=\"10000\">10,000 đ</label>\r\n        <label for=\"denomination2\"><input type=\"radio\" id=\"denomination2\" name=\"denomination\" value=\"20000\">20,000 đ</label>\r\n        <label for=\"denomination3\"><input type=\"radio\" id=\"denomination3\" name=\"denomination\" value=\"50000\">50,000 đ</label>\r\n        <label for=\"denomination4\"><input type=\"radio\" id=\"denomination4\" name=\"denomination\" value=\"100000\">100,000 đ</label>\r\n      </div>\r\n\r\n      <input type=\"submit\" value=\"Nhận Ngay\">\r\n    </form>\r\n  </div>\r\n\r\n\r\n  <div id=\"myModal\" class=\"modal\">\r\n    <div class=\"modal-content\">\r\n      <span class=\"close\">&times;</span>\r\n      <img src=\"https://www.emojiall.com/images/240/emojitwo/1f389.png\" width=\"145px\">\r\n      <h2>Chúc mừng!</h2>\r\n      <p> Bạn đã được vào danh sách, mã thẻ sẽ được gửi vào sms của bạn khi có kết quả </p>\r\n    </div>\r\n  </div>\r\n\r\n  <script>\r\n    var modal = document.getElementById(\"myModal\");\r\n    var closeBtn = document.getElementsByClassName(\"close\")[0];\r\n    function openModal() {\r\n      modal.style.display = \"block\";\r\n    }\r\n    function closeModal() {\r\n      modal.style.display = \"none\";\r\n    }\r\n\r\n    document.querySelector(\"form\").addEventListener(\"submit\", function(event) {\r\n      event.preventDefault();\r\n      openModal();\r\n    });\r\n    closeBtn.addEventListener(\"click\", closeModal);\r\n  </script>\r\n\r\n</body>\r\n</html>\r\n'),
